function toggleHeader(){let e=window.scrollY,s=document.getElementsByClassName("navbar")[0];if(s.classList.contains("no-scroll")){return}e<=0&&s.classList.contains("scrolling")?(s.classList.remove("scrolling"),s.classList.add("top")):e>0&&s.classList.contains("top")&&(s.classList.remove("top"),s.classList.add("scrolling"))}function scrollPastHeader(){let e=document.getElementsByClassName("navbar")[0].offsetHeight;window.scrollTo({top:window.innerHeight-e,left:0,behavior:"smooth"})}function scrollToTop(){window.scrollTo({top:0,left:0,behavior:"smooth"});return!1}function scrollToSection(e){let s=document.getElementsByClassName(e)[0].getBoundingClientRect().y,n=document.getElementsByClassName("navbar")[0].offsetHeight;window.scrollTo({top:pageYOffset+s-n,left:0,behavior:"smooth"});return!1}function showOnScroll(){let e=window.innerHeight,s=document.getElementsByClassName("scroll-to-hidden");for(let n of s)n.getBoundingClientRect().y<e*2/3&&(n.classList.add("scroll-to-shown"),n.classList.remove("scroll-to-hidden"))}function showOnLoad(){let e=document.getElementsByClassName("hidden-onload");for(let s=0;s<e.length;s++)e[s].classList.add("shown-onload"),e[s].classList.remove("hidden-onload")}function scrollListener(){$('.open').removeClass("oppenned");toggleHeader();showOnScroll()}function checkSection(){window.location.hash.length>1&&scrollToSection(window.location.hash.substring(1))}function toggleNavbar(){$(document).ready(function(){$(document).delegate('.open','click',function(e){$(this).addClass('oppenned');e.stopPropagation()});$(document).delegate('body','click',function(e){$('.open').removeClass('oppenned')});$(document).delegate('.cls','click',function(e){$('.open').removeClass('oppenned');e.stopPropagation()})})}window.addEventListener("scroll",scrollListener);window.onload=function(){scrollListener();showOnLoad();checkSection();toggleNavbar()}
